mapper.update:
UPDATE code_detail 
   SET code_name =#{codeName}
 WHERE class_code = #{classCode}
   AND code_value = #{codeValue}

mapper.delete:
DELETE FROM code_detail
 WHERE class_code = #{classCode}
   AND code_value = #{codeValue}

mapper.select:
SELECT IFNULL(MAX(sort_seq), 0)
  FROM code_detail
 WHERE class_code = #{classCode}
